import React, { Component } from 'react';
import PropTypes from 'prop-types';

import { AgGridReact } from 'ag-grid-react';

import 'ag-grid-community/dist/styles/ag-grid.css';
import 'ag-grid-community/dist/styles/ag-theme-balham.css';

const showAndHideOverlays = (gridApi, { loading, error, rows = [] }) => {
    if (!gridApi) {
        return;
    }

    const showLoading = loading;
    const showNoRowsOrError = error || rows.length === 0;

    if (showLoading && !error) {
        console.debug('[showAndHideOverlays] showLoadingOverlay');
        gridApi.hideOverlay();
        gridApi.showLoadingOverlay();
    }

    if (!showLoading && error) {
        console.debug('[showAndHideOverlays] showNoRowsOverlay');
        gridApi.hideOverlay();
        gridApi.showNoRowsOverlay();
    }

    if (!showLoading && !showNoRowsOrError) {
        console.debug('[showAndHideOverlays] hideOverlay');
        gridApi.hideOverlay();
    }
};

export default class Grid extends Component {

    static propTypes = {
        rows: PropTypes.array,
        columns: PropTypes.array,

        loading: PropTypes.bool,
        error: PropTypes.object

    };

    static defaultProps = {
        rows: [],
        columns: []
    };

    componentWillReceiveProps(nextProps, nextContext) {
        showAndHideOverlays(this.gridApi, nextProps);
    }

    onGridReady = (params) => {
        this.gridApi = params.api;
        this.columnApi = params.columnApi;

        showAndHideOverlays(this.gridApi, this.props);
    };

    render() {

        const { rows, columns } = this.props;

        return (
            <div
                className='grid-container'
                style={{ display: 'flex', width: '100%', height: '100%' }}
            >
                <div
                    className='ag-theme-balham'
                    style={{ height: '800px', width: '100%' }}
                >
                    <AgGridReact
                        suppressAggFuncInHeader={true}
                        columnDefs={columns}
                        onGridReady={(params) => this.onGridReady(params)}
                        rowData={rows}
                        enableSorting={true}
                        enableFilter={true}
                        animateRows={true}
                        enableColResize={true}
                        suppressCellSelection={true}
                        rowSelection="single"
                        deltaRowDataMode
                        getRowNodeId={data => data.orgName}
                    />
                </div>
            </div>)
            ;
    }
}
